package class08_Loops;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	

	
		int total=2;
		for(int k=1; k<4; k++) {
			total=total*k;
			System.out.print(total);
		}
		System.out.print("Final total="+total);
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	}

}
